import mydelete from "./Delete.vue";
export default {
  props: {
    showDelete: {
      type: Boolean,
      default: false
    },
    mylink: {
      type: String,
      default: ''
    }
  },
  data: function data() {
    return {
      current: 0,
      isShow: false,
      name: "",
      index: 0,
      odrname: '',
      datalist: []
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.odrname = this.$route.params.odrname;
    console.log("odrname" + this.odrname); //有值就获取参与的任务

    if (this.odrname) {
      this.datalist = JSON.parse(localStorage.getItem('project')) || [];
      this.datalist = this.datalist.filter(function (item) {
        return item.group.includes(_this.odrname);
      });
      console.log(this.datalist);
    } else {
      //否则获取管理的任务
      //获得全部项目
      this.datalist = JSON.parse(localStorage.getItem('project')) || [];
      console.log(this.datalist);
    }
  },
  methods: {
    todelete: function todelete(item, index) {
      // console.log('delete')
      this.name = item;
      this.index = index;
      this.isShow = true;
    },
    handleDelete: function handleDelete(index) {
      var list = JSON.parse(localStorage.getItem('project')) || [];
      list.splice(index, 1);
      localStorage.setItem('project', JSON.stringify(list));
      this.datalist = JSON.parse(localStorage.getItem('project'));
      this.isShow = false;
    },
    cancleDelete: function cancleDelete() {
      this.isShow = false;
    }
  },
  components: {
    mydelete: mydelete
  }
};
